SELECT MEMBER_NAME, REVIEW_TEXT, DATE_FORMAT(REVIEW_DATE, '%Y-%m-%d') REVIEW_DATE FROM (SELECT MEMBER_ID, COUNT(REVIEW_ID) FROM REST_REVIEW GROUP BY MEMBER_ID ORDER BY COUNT(REVIEW_ID) DESC limit 1) as result, MEMBER_PROFILE, REST_REVIEW WHERE result.MEMBER_ID = MEMBER_PROFILE.MEMBER_ID AND result.MEMBER_ID = REST_REVIEW.MEMBER_ID ORDER BY REVIEW_DATE, REVIEW_TEXT;