SELECT REST_INFO.REST_ID, REST_NAME, FOOD_TYPE, FAVORITES, ADDRESS, ROUND(SUM(REVIEW_SCORE)/COUNT(REVIEW_SCORE), 2) SCORE FROM REST_INFO, REST_REVIEW WHERE ADDRESS LIKE '서울%' AND REST_INFO.REST_ID = REST_REVIEW.REST_ID GROUP BY REST_ID ORDER BY ROUND(SUM(REVIEW_SCORE)/COUNT(REVIEW_SCORE), 2) DESC, FAVORITES DESC;