SELECT APNT_NO, PT_NAME, PATIENT.PT_NO PT_NO, DOCTOR.MCDP_CD MCDP_CD, DR_NAME, APNT_YMD FROM PATIENT, DOCTOR, APPOINTMENT WHERE DOCTOR.DR_ID = APPOINTMENT.MDDR_ID AND PATIENT.PT_NO = APPOINTMENT.PT_NO AND APPOINTMENT.MCDP_CD='CS' AND APNT_YMD BETWEEN '2022-04-13 00:00:00' AND '2022-04-13 23:59:59' AND APNT_CNCL_YN='N' ORDER BY APNT_YMD;
